import axios from "axios";
import React, { useEffect, useState } from "react";

export default function FilmCard(props) {
  const [listMovies, setListMovies] = useState(undefined);

  useEffect(() => {
    axios
      .get(
        "https://api.themoviedb.org/3/trending/all/day?api_key=42c75a6b80c35f26dc7f205b88001ee4"
      )
      .then((movies) => setListMovies(movies.data.results));
  }, []);

  return (
    <>
      {!listMovies ? (
        <>Still Loading</>
      ) : (
        <>
          {listMovies.map((element) => {
            return (
              <div key={element.id} class="card">
                <div
                  class="img1"
                  style={{
                    backgroundImage: `url(https://image.tmdb.org/t/p/w500${element.poster_path})`,
                  }}
                ></div>

                <div class="title">
                  {element.original_title || element.original_name}
                </div>
                <div class="text">{element.overview}</div>

                <div class="catagory">
                  {element.media_type === "tv" ? "Serie" : "Movie"}{" "}
                  <i class="fas fa-film"></i>
                </div>

                <div class="views">
                  {element.first_air_date}
                  <i class="far fa-eye"></i>{" "}
                </div>
              </div>
            );
          })}
        </>
      )}
    </>
  );
}
