import React, { useEffect, useState } from "react";
import { useLocation, useParams } from "react-router";
import { get } from "../utils/pedidos";
import Card from "./Card";

export default function SoloMovie() {
  const [movie, setMovie] = useState({});
  const showdataId = useLocation().pathname.split("/");
  if (showdataId[1] === "serie") showdataId[1] = "tv";
  useEffect(() => {
    get(`/${showdataId[1]}/${showdataId[2]}`).then((res) => {
      console.log(res);
      setMovie(res);
    });
  }, []);

  return (
    <div className="container">
      <div className="solo-movie">
        <div
          className="solo-movie-card"
          style={{
            backgroundColor: black,
          }}
        >
          <Card movie={movie}></Card>
        </div>
        <div className="space-and-text"> </div>
      </div>
    </div>
  );
}
