import axios from "axios";
import React, { useEffect, useState } from "react";

export default function FilmCard() {
  const [listMovies, setListMovies] = useState(undefined);

  useEffect(() => {
    axios
      .get(
        "https://api.themoviedb.org/3/trending/all/day?api_key=42c75a6b80c35f26dc7f205b88001ee4"
      )
      .then((movies) => setListMovies(movies.data.results));
  }, []);

  console.log(listMovies);

  return (
    <>
      {!listMovies ? (
        <>Still Loading</>
      ) : (
        <div class="card">
          <a href="#">
            <div
              class="img1"
              style={{
                backgroundImage: `url(https://image.tmdb.org/t/p/w500${movies[0].poster_path})`,
              }}
            >
              {" "}
            </div>
            <div class="img2"></div>
            <div class="title">{listMovies[0].name}</div>
            <div class="text">{listMovies[0].overview}</div>
            <a href="#">
              <div class="catagory">
                Series <i class="fas fa-film"></i>
              </div>
            </a>
            <a href="#">
              <div class="views">
                {listMovies[0].first_air_date.substring(0, 4)}
                <i class="far fa-eye"></i>{" "}
              </div>
            </a>
          </a>
        </div>
      )}
    </>
  );
}
