import React, { useEffect, useState } from "react";
import { useAuthContext } from "../context/AuthContext";
import { get, getUsersFavorites } from "../utils/pedidos";
import MovieGrid from "./MovieGrid";

export default function UserProfile() {
  const [listMovies, setListMovies] = useState([]);
  const { user } = useAuthContext();
  const [isLoading, setIsLoading] = useState(false);
  let arrFilms = [];
  let listMovieJson = [];
  useEffect(() => {
    //trae los favs del usuario pasado por parametro, y los transforma en objeto json
    getUsersFavorites(user)
      .then((favorites) => {
        return favorites.data.map((element) => {
          console.log(element);
          const { id, type } = JSON.parse(element);
          get(`/${type}/${id}`).then((pelicula) => {
            setListMovies([...listMovies, pelicula]);
          });
        });
      })
      .then(() => {
        setIsLoading(true);
      });
  }, []);

  if (!listMovies) return <div>Loading..</div>;

  return (
    <>
      {!isLoading ? (
        <div>Loading...</div>
      ) : (
        <div>
          <MovieGrid listMovies={listMovies} />
        </div>
      )}
    </>
  );
}
