import React, { useEffect, useState } from "react";
import { useLocation, useParams } from "react-router";
import { get } from "../utils/pedidos";
import Card from "./Card";
import CardSoloMovie from "./CardSoloMovie";

export default function SoloMovie() {
  const [movie, setMovie] = useState({});
  const showdataId = useLocation().pathname.split("/");
  if (showdataId[1] === "serie") showdataId[1] = "tv";
  useEffect(() => {
    get(`/${showdataId[1]}/${showdataId[2]}`).then((res) => {
      console.table(res);
      setMovie(res);
    });
  }, []);

  return (
    <div className="container">
      <div className="solo-movie">
        <div className="solo-movie-card">
          <div>
            <CardSoloMovie movie={movie}></CardSoloMovie>
          </div>
        </div>
        <div className="space-and-text">
          <div>
            <h1 className="solo-movie-title">
              {movie.original_title || movie.original_name}
            </h1>
            <span>
              {" "}
              {movie.first_air_date.substring(0, 4) ||
                movie.release_date.substring(0, 4)}
            </span>
            <p>Animacion,comedia,genre</p>
          </div>
          <div>
            <p>
              "The Targaryen dynasty is at the absolute apex of its power, with
              more than 15 dragons under their yoke. Most empires crumble from
              such heights. In the case of the Targaryens, their slow fall
              begins when King Viserys breaks with a century of tradition by
              naming his daughter Rhaenyra heir to the Iron Throne. But when
              Viserys later fathers a son, the court is shocked when Rhaenyra
              retains her status as his heir, and seeds of division sow friction
              across the realm."
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}
