import React, { useEffect, useState } from "react";
import { useAuthContext } from "../context/AuthContext";
import { get, getUsersFavorites } from "../utils/pedidos";
import MovieGrid from "./MovieGrid";

export default function UserProfile() {
  const [listMovies, setListMovies] = useState();
  const { user } = useAuthContext();
  const [isLoading, setIsLoading] = useState(true);
  let listMovieJson = [];
  useEffect(() => {
    //trae los favs del usuario pasado por parametro, y los transforma en objeto json
    getUsersFavorites(user).then((favorites) => {
      listMovieJson = favorites.data.map((element) => JSON.parse(element));

      let arrFilms = [];
      listMovieJson.map((movie) => {
        get(`/${movie.type}/${movie.id}`).then((res) => arrFilms.push(res));
      });
      setIsLoading(false);
    });
  }, []);

  if (!listMovies) return <div>Loading..</div>;

  return (
    <>
      {isLoading ? (
        <div>Loading...</div>
      ) : (
        <div>
          <MovieGrid listMovies={listMovies} />
        </div>
      )}
    </>
  );
}
