import axios from "axios";
import React, { useEffect, useState } from "react";

export default function FilmCard() {
  const [listMovies, setListMovies] = useState(undefined);

  useEffect(() => {
    axios
      .get(
        "https://api.themoviedb.org/3/trending/all/day?api_key=42c75a6b80c35f26dc7f205b88001ee4"
      )
      .then((movies) => setListMovies(movies.data.results));
  }, []);

  console.log(listMovies);

  return (
    <>
      {!listMovies ? (
        <>Still Loading</>
      ) : (
        <>
          {listMovies.map((element) => {
            console.log(element);
            return (
              <div class="card">
                <a href="#">
                  <div
                    class="img1"
                    style={{
                      backgroundImage: `url(https://image.tmdb.org/t/p/w500${element.poster_path})`,
                    }}
                  ></div>
                  <div
                    class="img2"
                    style={{
                      backgroundImage: `url(https://image.tmdb.org/t/p/w500${element.poster_path})`,
                    }}
                  ></div>
                  <div class="title">{element.name}</div>
                  <div class="text">{element.overview}</div>
                  <a href="#">
                    <div class="catagory">
                      Series <i class="fas fa-film"></i>
                    </div>
                  </a>
                  <a href="#">
                    <div class="views">
                      {element.first_air_date.substring(0, 4)}
                      <i class="far fa-eye"></i>{" "}
                    </div>
                  </a>
                </a>
              </div>
            );
          })}
        </>
      )}
    </>
  );
}
