import axios from "axios";
import { useEffect, useState } from "react";

export default function FilmSearch(query, pageNumber) {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);
  const [movies, setMovies] = useState([]);
  const [hasMore, setHasMore] = useState(false);

  const url =
    "https://api.themoviedb.org/3/search/multi?api_key=42c75a6b80c35f26dc7f205b88001ee4&language=en-US&query=batman&page=1";

  useEffect(() => {
    setMovies([]);
  }, [query]);

  useEffect(() => {
    setLoading(true);
    setError(false);
    axios({ method: "GET", url, params: { q: query, page: pageNumber } }).then(
      (res) => {
        setMovies((prevMovies) => {
          return [...prevMovies, ...res.data.results];
        });
        setHasMore(res.data.results > 0);
        setLoading(false);
      }
    );
  }, [query, pageNumber]);
  return { loading, error, movies, hasMore };
}
